mapper.insert:
INSERT INTO financial_portfolio (
    user_id, total_assets, investment_profile, cash, deposit,
    savings, bond, fund, stock, other
) VALUES (
             #{userId}, #{totalAssets},
             #{investmentProfile}, #{cash}, #{deposit},
             #{savings}, #{bond}, #{fund}, #{stock}, #{other}
         )

mapper.select:
SELECT *
FROM financial_portfolio
WHERE user_id = #{userId}
  AND DATE(created_at) = #{date}
